mapper.select:
SELECT ARTICLE_ID
     , COMMENT_ID
  FROM ARTICLE_COMMENT
 WHERE ARTICLE_ID = #{articleId}
   AND COMMENT_ID = #{commentId}

SELECT COMMENT_ID
  FROM ARTICLE_COMMENT
 WHERE ARTICLE_ID = #{articleId}

mapper.insert:
INSERT
  INTO ARTICLE_COMMENT
( ARTICLE_ID
, COMMENT_ID
)
  VALUES
(
  #{article.articleId}
, #{article.commentId}
)
