mapper.select:
SELECT ARTICLE_ID
     , USER_ID
  FROM ARTICLE_LIKE
 WHERE ARTICLE_ID = #{articleId}
   AND USER_ID = #{userId}

mapper.insert:
INSERT
  INTO ARTICLE_LIKE
( ARTICLE_ID
, USER_ID
)
  VALUES
( #{articleId}
, #{userId}
)

mapper.delete:
DELETE
  FROM ARTICLE_LIKE
 WHERE ARTICLE_ID = #{articleId}
   AND USER_ID = #{userId}
