mapper.select:
SELECT FILE_NM
  FROM FILE_TABLE
 WHERE FILE_ID = #{dirId}

mapper.insert:
INSERT
  INTO FILE_TABLE
  ( FILE_NM
  , FILE_ORG_NM
  , FILE_PATH
  , TYPE
  , PARENT_ID
  , GROUP_ID
  , REG_DTM
  , REG_ID
  , MOD_DTM
  , MOD_ID
  )
  VALUES
  (
    #{dirNm}
  , #{dirNm}
  , #{path}
  , 'DIR'
  , #{parentId}
  , #{groupId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{userId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{userId}
  )

INSERT
  INTO FILE_TABLE
  ( FILE_NM
  , FILE_ORG_NM
  , FILE_PATH
  , TYPE
  , PARENT_ID
  , GROUP_ID
  , REG_DTM
  , REG_ID
  , MOD_DTM
  , MOD_ID
  )
  VALUES
  (
    #{fileNm}
  , #{fileOrgNm}
  , #{path}
  , 'FILE'
  , #{parentId}
  , #{groupId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{userId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{userId}
  )
