mapper.insert:
INSERT
  INTO GROUP_TABLE
  ( GROUP_NM
  , GROUP_MST_USER_ID
  , REG_DTM
  , REG_ID
  , MOD_DTM
  , MOD_ID
  )
  VALUES
  (
    #{group.groupNm}
  , #{group.groupMstUserId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{group.groupMstUserId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{group.groupMstUserId}
  )

INSERT
INTO GROUP_USER
( GROUP_ID
, USER_ID
, REG_DTM
, REG_ID
, MOD_DTM
, MOD_ID
)
VALUES
  (
    #{group.groupId}
  , #{userId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{group.groupMstUserId}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{group.groupMstUserId}
  )

mapper.select:
SELECT COUNT(GROUP_ID)
  FROM GROUP_USER
 WHERE GROUP_ID = #{group.groupId}
   AND USER_ID  = #{userId}

mapper.delete:
DELETE
  FROM GROUP_USER
 WHERE GROUP_ID = #{group.groupId}
   AND USER_ID  = #{group.userId}

mapper.update:
UPDATE GROUP_TABLE
   SET GROUP_MST_USER_ID = #{userId}
     , MOD_DTM = DATE_FORMAT(NOW(), '%Y%m%d')
     , MOD_ID = #{group.groupMstUserId}
 WHERE GROUP_ID = #{group.groupId}
