mapper.insert:
INSERT
  INTO USER_FRIEND_TABLE
VALUES (
         #{friendId}
       , #{targetUserId}
       , #{relationship}
       , DATE_FORMAT(NOW(), '%Y%m%d')
       , #{userId}
       , DATE_FORMAT(NOW(), '%Y%m%d')
       , #{userId}
       )

mapper.delete:
DELETE
  FROM USER_FRIEND_TABLE
 WHERE FRIEND_ID = #{friendId}
   AND USER_ID = #{targetUserId}
