mapper.insert:
INSERT
  INTO USER_TABLE
VALUES (
         #{user.userId}
       , #{user.name}
       , #{user.email}
       , #{user.nickName}
       , NULL
       , #{user.loginType}
       , DATE_FORMAT(NOW(), '%Y%m%d')
       , DATE_FORMAT(NOW(), '%Y%m%d')
       , #{user.userId}
       , DATE_FORMAT(NOW(), '%Y%m%d')
       , #{user.userId}
       )

mapper.update:
UPDATE USER_TABLE
   SET NICK_NAME = #{user.nickName}
     , PICTURE_ID = #{user.pictureId}
     , LAST_LOGIN_DATE = DATE_FORMAT(NOW(), '%Y%m%d')
     , MOD_DTM = DATE_FORMAT(NOW(), '%Y%m%d')
 WHERE USER_ID = #{user.userId}

UPDATE USER_TABLE
SET PICTURE_ID = #{fileId},
    MOD_DTM = DATE_FORMAT(NOW(), '%Y%m%d'),
    MOD_ID  = #{userId}
WHERE USER_ID = #{userId}
